import React, { useState, useEffect } from "react";
import styled from "styled-components";
const Arena = require("are.na");

const arena = new Arena({
  accessToken:
    "1986df47db6a294df0575f57e7d655cc5e6ea1bfe87da5a47be605d717fc0086",
});

const Photo = styled.img`
  width: 0vw;
`;
const Caption = styled.p`
  font-size: 1em;
  font-family: Helvetica, sans-serif;
`;
const DivContainer = styled.div`
  height: 100vh;
  width: 100vw;
  cursor: pointer;
`;
const DivFlex = styled.div`
  display: flex;
  flex-direction: row-reverse;
  flex-wrap: wrap;
  gap: 1em;
`;
const PhotoContainer = styled.div``;

export function PhotoFeed() {
  let [photos, setPhotos] = useState([]);
  let [gallery, setGallery] = useState([]);
  let [counter, SetCounter] = useState(1);

  useEffect(() => {
    arena
      .channel("photodrop")
      .contents()
      .then((contents) => {
        setPhotos(contents);
      })
      .then(() =>
        setGallery((prevGallery) => [...prevGallery, photos[counter]])
      )
      .catch((err) => console.log(err));
  }, []);

  function AppendPhoto() {
    SetCounter((prevCounter) => prevCounter + 1);
    setGallery((prevGalleries) => [...prevGalleries, photos[counter + 1]]);
  }
  if (gallery.length === 0) {
    return <>still loading </>;
  }

  function Galleries() {
    return (
      <>
        <DivFlex>
          {gallery.slice(1).map((gallery, index) => (
            <PhotoContainer>
              <Photo
                src={gallery.image ? gallery.image.display.url : null}
                className={index % 2 == 1 ? "grow" : "shrink"}
              />
              <Caption> {gallery.title ? gallery.title : null} </Caption>
            </PhotoContainer>
          ))}
        </DivFlex>
      </>
    );
  }

  return (
    <DivContainer onClick={AppendPhoto} photos={photos}>
      <Galleries gallery={gallery}> </Galleries>
    </DivContainer>
  );
}
