import React, { useState, useEffect } from "react";
import Webamp from "webamp";
const Arena = require("are.na");

function MapSongs(props) {
  return (
    <ul>
      {props.songs
        .slice(0)
        .reverse()
        .map((song, index) => (
          <li key={song.id}>
            <p>{index}</p>
            <img class="thumbnail" src={song.image.display.url}></img>
            <a href={song.source.url}> {song.title} </a>
          </li>
        ))}
    </ul>
  );
}
export function SongFeed() {
  const arena = new Arena({
    accessToken:
      "1986df47db6a294df0575f57e7d655cc5e6ea1bfe87da5a47be605d717fc0086",
  });

  let [songs, setSongs] = useState([]);

  useEffect(() => {
    arena
      .channel("la-musica")
      .contents({ per: 20 })
      .then((contents) => setSongs(contents))
      .catch((err) => console.log(err));

    const webamp = new Webamp({
      initialTracks: [
        {
          metaData: {
            artist: " Art of Noise",
            title: "Moments in Love (slowed + pitched)",
          },

          url:
            "../assets/music/Moment in Love - Art of Noise (slowed + pitched).mp3",
        },
      ],
      initialSkin: {
        url: "../assets/skins/apple_amp.wsz",
      },
    });

    webamp.renderWhenReady(document.getElementById("winamp-container"));
  }, []);

  shuffleArray(songs);

  function shuffleArray(songs) {
    for (let i = songs.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      const temp = songs[i];
      songs[i] = songs[j];
      songs[j] = temp;
    }
  }

  return (
    <div>
      <div id="winamp-container"></div>
      {<MapSongs songs={songs}> </MapSongs>}
    </div>
  );
}
