import React, { useState, useEffect } from "react";
import styled from "styled-components";
const Arena = require("are.na");

const Photo = styled.img`
  width: 0vw;
`;
const Caption = styled.p`
  font-size: 1em;
  font-family: Helvetica, sans-serif;
`;
const DivContainer = styled.div`
  height: 100vh;
  width: 100vw;
  cursor: pointer;
`;
const DivFlex = styled.div`
  display: flex;
  flex-direction: row-reverse;
  flex-wrap: wrap;
  gap: 1em;
`;
const PhotoContainer = styled.div``;

function MapPhotos(props) {
  return (
    <ul>
      {props.photos
        .slice(0)
        .reverse()
        .map((photo, index) => (
          <List key={photo.id}>
            <Photo src={photo.image.display.url} />
            <Caption> {photo.title} </Caption>
          </List>
        ))}
    </ul>
  );
}
const List = styled.li`
  text-decoration: none;
  display: block;
`;
const ClickMe = styled.h1`
  color: white;
  border: 2px solid blue;
  background-color: silver;
  text-align: center;
  width: 5em;
`;

export function PhotoFeed() {
  let [photos, setPhotos] = useState([]);
  let [gallery, setGallery] = useState([photos[2]]);
  let [counter, SetCounter] = useState(0);

  useEffect(() => {
    arena
      .channel("photodrop")
      .contents()
      .then((contents) => {
        setPhotos(contents);
      })
      .catch((err) => console.log(err));
  }, []);

  const arena = new Arena({
    accessToken:
      "1986df47db6a294df0575f57e7d655cc5e6ea1bfe87da5a47be605d717fc0086",
  });

  function Gallery(props) {
    return (
      <>
        <DivFlex>
          {props.gallery.slice(1).map((gallery, index) => (
            <PhotoContainer>
              <Photo
                src={gallery.image ? gallery.image.display.url : null}
                className={index % 2 == 1 ? "grow" : "shrink"}
              />
              <Caption> {gallery.title ? gallery.title : null} </Caption>
            </PhotoContainer>
          ))}
        </DivFlex>
      </>
    );
  }

  const AppendPhoto = (props) => {
    SetCounter(counter + 1);
    setGallery([...gallery, photos[counter]]);

    return null;
  };

  return (
    <DivContainer onClick={AppendPhoto} photos={photos} gallery={gallery}>
      {<Gallery gallery={gallery}> </Gallery>}
    </DivContainer>
  );
}
