import React, { useState, useEffect } from "react";
import styled from "styled-components";
const Arena = require("are.na");

const Photo = styled.img`
  width: 30%;
  height: 40%;
  object-fit: fill;
  display: block;
  margin: auto;
`;
const Caption = styled.p`
  font-size: 3em;
  font-family: Arial, Helvetica, sans-serif;
  display: block;
  margin: auto;
  text-align: center;
`;

function Gallery(props) {
  return (
    <div>
      {props.gallery.map((gallery, index) => (
        <List key={gallery.id}>
          <Photo src={gallery.image.display.url} />
          <Caption> {gallery.title} </Caption>
        </List>
      ))}
    </div>
  );
}

const AppendPhoto = (props) => {
  console.log(photos[1]);
  // setGallery([...props.gallery, photos[1]]);

  return null;
};

function MapPhotos(props) {
  return (
    <ul>
      {props.photos
        .slice(0)
        .reverse()
        .map((photo, index) => (
          <List key={photo.id}>
            <Photo src={photo.image.display.url} />
            <Caption> {photo.title} </Caption>
          </List>
        ))}
    </ul>
  );
}
const List = styled.li`
  text-decoration: none;
  display: block;
`;

export function PhotoFeed() {
  const arena = new Arena({
    accessToken:
      "1986df47db6a294df0575f57e7d655cc5e6ea1bfe87da5a47be605d717fc0086",
  });

  useEffect(() => {
    arena
      .channel("photodrop")
      .contents()
      .then((contents) => setPhotos(contents))
      .catch((err) => console.log(err));
  }, []);
  let [gallery, setGallery] = useState([]);
  let [photos, setPhotos] = useState([]);

  return (
    <div onClick={AppendPhoto} photos={photos} gallery={gallery}>
      {/* <Gallery> </Gallery> */}
      {<MapPhotos photos={photos}> </MapPhotos>}
    </div>
  );
}
