import React from "react";
import styled from "styled-components";
export function About() {
  var i = 0;
  var txt =
    " I am  Michael Elijah! I tinker in the realms of Web-Development, Augmented Reality, Film-Making, and Design.";

  /* The text */
  var speed = 80; /* The speed/duration of the effect in milliseconds */

  function typeWriter() {
    if (i < txt.length && document.getElementById("bio")) {
      document.getElementById("bio").innerHTML += txt.charAt(i);
      i++;
      setTimeout(typeWriter, speed);
      document.getElementById("me").style.bottom = "0";
    } else {
      return null;
    }
  }

  const About = styled.p`
    position: relative;
    left: 1vw;
    font-size: 4.4vw;
    color: blue;
    font-family: Arial, Helvetica, sans-serif;
    width: 80vw;
    height: 40vw;
    padding: 1em;
    margin: 0;
  `;
  const Me = styled.img`
    position: absolute;
    bottom: -40%;
    right: 5%;
    height: 40%;
    transition: ease-out 0.4s;
  `;

  return (
    <div>
      <About onMouseEnter={typeWriter} id="bio">
        {" "}
        Hiii!
      </About>
      <Me src="../assets/img/me.png" alt="me" id="me" />
    </div>
  );
}
