import React, { useEffect } from "react";
import styled from "styled-components";
export function About() {
  const [audio, SetAudio] = useState("");
  const Playit = () => {
    audio.play();
  };
  const Stopit = () => {
    audio.pause();
  };

  var i = 0;
  var txt =
    " I am Michael! I explore the realms of web-development, augmented reality, film, and design.";

  /* The text */
  var speed = 80; /* The speed/duration of the effect in milliseconds */

  function typeWriter() {
    if (i < txt.length && document.getElementById("bio")) {
      document.getElementById("bio").innerHTML += txt.charAt(i);
      i++;
      setTimeout(typeWriter, speed);
      document.getElementById("me").style.bottom = "0";
    } else {
      return null;
    }
  }

  const About = styled.p`
    position: relative;
    left: 1vw;
    font-size: 4.4vw;
    color: blue;
    font-family: Arial, Helvetica, sans-serif;
    width: 80vw;
    height: 40vw;
    padding: 1em;
    margin: 0;
  `;
  const Me = styled.img`
    position: absolute;
    bottom: -40%;
    right: 5%;
    height: 40%;
    transition: ease-out 0.4s;
  `;
  useEffect(() => {
    typeWriter();
    SetAudio(new Audio("../assets/music/sound-on.mp4"));
    Playit();
  }, 100);

  return (
    <div>
      <audio className="audio-element">
        <source src="../assets/music/sound-on.mp4"></source>
      </audio>
      <About id="bio">
        Hiii! <hr />
      </About>
      <Me src="../assets/img/me.png" alt="me" id="me" />
    </div>
  );
}
