import React, { useState, useEffect } from "react";
import Webamp from "webamp";
const Arena = require("are.na");

function MapSongs(props) {
  return (
    <ul>
      {props.songs
        .slice(0)
        .reverse()
        .map((song, index) => (
          <li key={song.id}>
            <p>{index}</p>
            <img class="thumbnail" src={song.image.display.url}></img>
            <a href={song.source.url}> {song.title} </a>
          </li>
        ))}
    </ul>
  );
}

export function SongFeed() {
  const webamp = new Webamp({
    // Optional.
    initialTracks: [
      {
        metaData: {
          artist: "DJ Mike Llama",
          title: "Llama Whippin' Intro",
        },
        // NOTE: Your audio file must be served from the same domain as your HTML
        // file, or served with permissive CORS HTTP headers:
        // https://developer.mozilla.org/en-US/docs/Web/HTTP/CORS
        // Can be downloaded from: https://github.com/captbaritone/webamp/raw/master/mp3/llama-2.91.mp3
        url: "path/to/mp3/llama-2.91.mp3",
      },
    ],
    // Optional. The default skin is included in the js bundle, and will be loaded by default.
    initialSkin: {
      // NOTE: Your skin file must be served from the same domain as your HTML
      // file, or served with permissive CORS HTTP headers:
      // https://developer.mozilla.org/en-US/docs/Web/HTTP/CORS
      // Can be downloaded from https://github.com/captbaritone/webamp/raw/master/skins/TopazAmp1-2.wsz
      url: "../assets/skins/apple_amp.wsz",
    },
  });

  const arena = new Arena({
    accessToken:
      "1986df47db6a294df0575f57e7d655cc5e6ea1bfe87da5a47be605d717fc0086",
  });

  let [songs, setSongs] = useState([]);

  useEffect(() => {
    arena
      .channel("la-musica")
      .contents({ per: 20 })
      .then((contents) => setSongs(contents))
      .catch((err) => console.log(err));
  }, []);

  shuffleArray(songs);

  function shuffleArray(songs) {
    for (let i = songs.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      const temp = songs[i];
      songs[i] = songs[j];
      songs[j] = temp;
    }
  }

  return (
    <div>
      {<MapSongs songs={songs}> </MapSongs>}

      <div id="winamp-container"></div>
    </div>
  );
}
