import React, { useState, useEffect } from "react";
import styled from "styled-components";
const Arena = require("are.na");

const Photo = styled.img`
  width: 30%;
  height: 40%;
  object-fit: fill;
  display: block;
  margin: auto;
`;
const Caption = styled.p`
  font-size: 3em;
  font-family: Arial, Helvetica, sans-serif;
  display: block;
  margin: auto;
  text-align: center;
`;
const DivContainer = styled.div`
  height: 100vj;
  width: 100vw;
`;

function MapPhotos(props) {
  return (
    <ul>
      {props.photos
        .slice(0)
        .reverse()
        .map((photo, index) => (
          <List key={photo.id}>
            <Photo src={photo.image.display.url} />
            <Caption> {photo.title} </Caption>
          </List>
        ))}
    </ul>
  );
}
const List = styled.li`
  text-decoration: none;
  display: block;
`;

export function PhotoFeed() {
  let [photos, setPhotos] = useState([]);
  let [gallery, setGallery] = useState([photos]);
  let [counter, SetCounter] = useState(0);

  const arena = new Arena({
    accessToken:
      "1986df47db6a294df0575f57e7d655cc5e6ea1bfe87da5a47be605d717fc0086",
  });

  function Gallery(props) {
    return props.gallery ? (
      <div>
        <button> reveal </button>

        <img src={gallery.image} />

        {props.gallery.map((gallery, index) => (
          <List key={gallery.id}>
            <Photo src={gallery.image ? gallery.image.display.url : null} />
            <Caption> {gallery.title ? gallery.title : null} </Caption>
          </List>
        ))}
      </div>
    ) : null;
  }

  const AppendPhoto = (props) => {
    SetCounter(counter + 1);
    setGallery([...gallery, photos[counter]]);

    return null;
  };

  useEffect(() => {
    arena
      .channel("photodrop")
      .contents()
      .then((contents) => setPhotos(contents))
      .catch((err) => console.log(err));
  }, []);

  return (
    <DivContainer onClick={AppendPhoto} photos={photos} gallery={gallery}>
      {<Gallery gallery={gallery}> </Gallery>}
    </DivContainer>
  );
}
