import React from "react";
import styled from "styled-components";
export function About() {
  var i = 0;
  var txt =
    "I am interested in the web as a social space. Life on the clearnet has been diminished the sense of belonging within the artist & proletariot class of the web. We have opted to crawl down to the emerging cozy web to find our communities once again.";

  /* The text */
  var speed = 50; /* The speed/duration of the effect in milliseconds */

  function typeWriter() {
    if (i < txt.length) {
      document.getElementById("bio").innerHTML += txt.charAt(i);
      i++;
      setTimeout(typeWriter, speed);
    }
  }

  const About = styled.p`
    position: relative;
    left: 1vw;
    font-size: 4.4vw;
    color: blue;
    font-family: Arial, Helvetica, sans-serif;
    width: 80vw;
    height: 40vw;
    padding: 1em;
    margin: 0;
  `;
  const Me = styled.img`
    position: absolute;
    bottom: 0%;
    right: 10%;
    height: 30%;
  `;

  return (
    <div>
      <About onInit={typeWriter} id="bio"></About>

      <Me src="../assets/img/me.png" alt="me" />
    </div>
  );
}
